线段树NB网友Blog(https://blog.csdn.net/huangzihaoal/article/details/81813454) 线段树，是一种二叉搜索树。它将一段区间划分为若干单位区间，每一个节点都储存着一个区间。 它功能强大，支持区间求和，区间最大值，区间修改，单点修改等操作。 线段树的每一个节点都储存着一段区间[L…R]的信息，其中叶子节点L=R。 它的大致思想是：将一段大区间平均地划分成2个小区间，每一个小区间都再平均分成2个更小区间……以此类推， 直到每一个区间的L等于R（这样这个区间仅包含一个节点的信息，无法被划分）。通过对这些区间进行修改、查询，来实现对大区间的修改、查询。 这样一来，每一次修改、查询的时间复杂度都只为O(logn) 但是，可以用线段树维护的问题必须满足区间加法，否则是不可能将大问题划分成子问题来解决的。 初阶线段树单点修改区间查询区间求和//HDU 1166 单点更新区间求和 #include&lt;bits/stdc++.h&gt; using namespace std; #define lson l,m,rt&lt;&lt;1 #define rson m+1,r,rt&lt;&lt;1|1 const int maxn=1e5+5; int sum[maxn&lt;&lt;2],a[maxn],n; void pushup(int rt) {sum[rt]=sum[rt&lt;&lt;1]+sum[rt&lt;&lt;1|1];} void build(int l,int r,int rt){ if(l==r) {sum[rt]=a[l];return;} int m=(l+r)&gt;&gt;1;build(lson);build(rson);pushup(rt); } void update(int p,int t,int l,int r,int rt){ if(l==r) {sum[rt]+=t;return;} int m=(l+r)&gt;&gt;1; if(p&lt;=m) update(p,t,lson); else update(p,t,rson); pushup(rt); } int query(int L,int R,int l,int r,int rt){ if(L&lt;=l&amp;&amp;R&gt;=r) return sum[rt]; int m=(l+r)&gt;&gt;1,ans=0; if(L&lt;=m) ans+=query(L,R,lson); if(R&gt;m) ans+=query(L,R,rson); return ans; } int main() { ios::sync_with_stdio(0);cin.tie(0); int t;cin&gt;&gt;t;int ca=1; while(t--){ cin&gt;&gt;n;for(int i=1;i&lt;=n;i++) cin&gt;&gt;a[i]; cout&lt;&lt;&quot;Case &quot;&lt;&lt;ca++&lt;&lt;&quot;:&quot;&lt;&lt;endl;build(1,n,1);string s; while(cin&gt;&gt;s){ if(s[0]==&apos;E&apos;) break;int x,y;cin&gt;&gt;x&gt;&gt;y; if(s[0]==&apos;Q&apos;) cout&lt;&lt;query(x,y,1,n,1)&lt;&lt;endl; else if(s[0]==&apos;A&apos;) update(x,y,1,n,1); else update(x,-y,1,n,1); } } } 区间最值//HDU-1754 单点更新区间最值 #include&lt;bits/stdc++.h&gt; using namespace std; #define lson l,m,rt&lt;&lt;1 #define rson m+1,r,rt&lt;&lt;1|1 const int maxn=2e5+5; int ans[maxn&lt;&lt;2],a[maxn],n,m; void pushup(int rt) {ans[rt]=max(ans[rt&lt;&lt;1],ans[rt&lt;&lt;1|1]);} void build(int l,int r,int rt){ if(l==r) {ans[rt]=a[l];return;} int m=(l+r)&gt;&gt;1;build(lson);build(rson);pushup(rt); } void update(int p,int t,int l,int r,int rt){ if(l==r){ans[rt]=t;return;} int m=(l+r)&gt;&gt;1; if(p&lt;=m) update(p,t,lson); else update(p,t,rson); pushup(rt); } int query(int L,int R,int l,int r,int rt){ if(L&lt;=l&amp;&amp;R&gt;=r) return ans[rt]; int m=(l+r)&gt;&gt;1,ret=0; if(L&lt;=m) ret=max(ret,query(L,R,lson)); if(R&gt;m) ret=max(ret,query(L,R,rson)); return ret; } int main() { ios::sync_with_stdio(0);cin.tie(0); while(cin&gt;&gt;n&gt;&gt;m){ for(int i=1;i&lt;=n;i++) cin&gt;&gt;a[i]; build(1,n,1);char c;int x,y; while(cin&gt;&gt;c&gt;&gt;x&gt;&gt;y){ if(c==&apos;U&apos;) update(x,y,1,n,1); else cout&lt;&lt;query(x,y,1,n,1)&lt;&lt;endl; } } } 区间修改区间查询需要lazy标记 区间替换//HDU-1698 区间替换 #include&lt;bits/stdc++.h&gt; using namespace std; #define lson l,m+1,rt&lt;&lt;1 #define rson m+1,r,rt&lt;&lt;1|1 const int maxn=1e5+5; int sum[maxn&lt;&lt;2],lazy[maxn&lt;&lt;2],a[maxn],n; void pushup(int rt){ sum[rt]=sum[rt&lt;&lt;1]+sum[rt&lt;&lt;1|1]; } void pushdown(int rt,int m){ if(lazy[rt]){ lazy[rt&lt;&lt;1]=lazy[rt&lt;&lt;1|1]=lazy[rt]; sum[rt&lt;&lt;1]=(m-(m&gt;&gt;1))*lazy[rt]; sum[rt&lt;&lt;1|1]=(m&gt;&gt;1)*lazy[rt]; lazy[rt]=0; } } void build(int l,int r,int rt){ lazy[rt]=0; if(l==r) {sum[rt]=a[l];return;} int m=(l+r)&gt;&gt;1; build(lson);build(rson);pushup(rt); } void update(int L,int R,int c,int l,int r,int rt){ if(L&lt;=l&amp;&amp;R&gt;=r){ lazy[rt]=c;sum[rt]=(r-l+1)*c;return; } pushdown(rt,r-l+1); int m=(l+r)&gt;&gt;1; if(L&lt;=m) update(L,R,c,lson); if(R&gt;m) update(L,R,c,rson); pushup(rt); } int query(int L,int R,int l,int r,int rt){ if(L&lt;=l&amp;&amp;R&gt;=r) return sum[rt]; pushdown(rt,r-l+1); int m=(l+r)&gt;&gt;1; int ans=0; if(L&lt;=m) ans+=query(L,R,lson); if(R&gt;m) ans+=query(L,R,rson); cout&lt;&lt;ans&lt;&lt;endl; } int main() { ios::sync_with_stdio(0);cin.tie(0); int t,n,m,ca=1;cin&gt;&gt;t; while(t--){ cin&gt;&gt;n&gt;&gt;m; for(int i=1;i&lt;=n;i++) cin&gt;&gt;a[i]; build(1,n,1); while(m--){ int x,y,z;cin&gt;&gt;x&gt;&gt;y&gt;&gt;z; update(x,y,z,1,n,1); } cout&lt;&lt;&quot;Case &quot;&lt;&lt;ca++&lt;&lt;&quot;: The total value of the hook is &quot;&lt;&lt;sum[1]&lt;&lt;&quot;.&quot;&lt;&lt;endl; } } 区间增减//POJ-3468 区间增减 #include&lt;iostream&gt; typedef long long ll; using namespace std; #define lson l,m,rt&lt;&lt;1 #define rson m+1,r,rt&lt;&lt;1|1 const int maxn=1e5+5; ll sum[maxn&lt;&lt;2],lazy[maxn&lt;&lt;2],a[maxn]; int n,m; void pushup(int rt){ sum[rt]=sum[rt&lt;&lt;1]+sum[rt&lt;&lt;1|1]; } void build(int l,int r,int rt){ lazy[rt]=0; if(l==r) {sum[rt]=a[l];return;} int m=(l+r)&gt;&gt;1; build(lson);build(rson);pushup(rt); } void pushdown(int rt,int m){ if(lazy[rt]){ lazy[rt&lt;&lt;1]+=lazy[rt]; lazy[rt&lt;&lt;1|1]+=lazy[rt]; sum[rt&lt;&lt;1]+=(m-(m&gt;&gt;1))*lazy[rt]; sum[rt&lt;&lt;1|1]+=((m&gt;&gt;1))*lazy[rt]; lazy[rt]=0; } } void update(int L,int R,int c,int l,int r,int rt){ if(L&lt;=l&amp;&amp;R&gt;=r){sum[rt]+=(r-l+1)*c;lazy[rt]+=c;return;} pushdown(rt,r-l+1); int m=(l+r)&gt;&gt;1; if(L&lt;=m) update(L,R,c,lson); if(R&gt;m) update(L,R,c,rson); pushup(rt); } ll query(int L,int R,int l,int r,int rt){ if(L&lt;=l&amp;&amp;R&gt;=r) return sum[rt]; pushdown(rt,r-l+1); int m=(l+r)&gt;&gt;1; ll ans=0; if(L&lt;=m) ans+=(ll)query(L,R,lson); if(R&gt;m) ans+=(ll)query(L,R,rson); return ans; } int main() { ios::sync_with_stdio(0);cin.tie(0); while(cin&gt;&gt;n&gt;&gt;m){ for(int i=1;i&lt;=n;i++) cin&gt;&gt;a[i]; build(1,n,1); char c;int x,y,z; while(m--){ cin&gt;&gt;c&gt;&gt;x&gt;&gt;y; if(c==&apos;Q&apos;) cout&lt;&lt;query(x,y,1,n,1)&lt;&lt;endl; else {cin&gt;&gt;z;update(x,y,z,1,n,1);} } } } 中阶线段树乘法线段树//https://www.luogu.org/problem/P3373 题意：三种操作：将某区间每一个数乘上x、将某区间每一个数加上x、求出某区间每一个数的和 思路：注意加法和乘法的优先级，维护两种lazy标记。 输入： 输出： 5 5 38 17 1 5 4 2 3 2 2 1 4 1 3 2 5 1 2 4 2 2 3 5 5 3 1 4 #include&lt;bits/stdc++.h&gt; #define lson l,m,rt&lt;&lt;1 #define rson m+1,r,rt&lt;&lt;1|1 using namespace std; const int maxn=1e5+5; typedef long long ll; ll sum[maxn&lt;&lt;2],mlz[maxn&lt;&lt;2],plz[maxn&lt;&lt;2]; ll n,m,p; inline void pushup(int rt){sum[rt]=(sum[rt&lt;&lt;1]+sum[rt&lt;&lt;1|1])%p;} inline void Build(int l,int r,int rt){ mlz[rt]=1;plz[rt]=0; if(l==r){ll t;cin&gt;&gt;t;sum[rt]=t%p;return;} int m=(l+r)&gt;&gt;1;Build(lson);Build(rson);pushup(rt); } inline void pushdown(int rt,int m){ ll k1=mlz[rt],k2=plz[rt]; sum[rt&lt;&lt;1]=(sum[rt&lt;&lt;1]*k1+(m-(m&gt;&gt;1))*k2)%p;//左边和乘以乘法lazy+元素总数*加法lazy sum[rt&lt;&lt;1|1]=(sum[rt&lt;&lt;1|1]*k1+((m&gt;&gt;1))*k2)%p;//右边和乘以乘法lazy+元素总数*加法lazy mlz[rt&lt;&lt;1]=(mlz[rt&lt;&lt;1]*k1)%p;//乘法标记向左下推 mlz[rt&lt;&lt;1|1]=(mlz[rt&lt;&lt;1|1]*k1)%p;//乘法标记向右下推 plz[rt&lt;&lt;1]=(plz[rt&lt;&lt;1]*k1+k2)%p;//左下加法lazy*父节点的乘法lazy+左下乘法lazy plz[rt&lt;&lt;1|1]=(plz[rt&lt;&lt;1|1]*k1+k2)%p;//右下加法lazy*父节点的乘法lazy+左下乘法lazy mlz[rt]=1;plz[rt]=0;//lazy标记置位 } inline void add(int L,int R,int x,int l,int r,int rt){ if(L&lt;=l&amp;&amp;R&gt;=r){ sum[rt]+=(r-l+1)*x%p; plz[rt]=(plz[rt]+x)%p;//修改加法lazy return; } pushdown(rt,r-l+1); int m=(l+r)&gt;&gt;1; if(L&lt;=m) add(L,R,x,lson); if(R&gt;m) add(L,R,x,rson); pushup(rt); } inline void mul(int L,int R,int x,int l,int r,int rt){ if(L&lt;=l&amp;&amp;R&gt;=r){ sum[rt]=sum[rt]*x%p; mlz[rt]=(mlz[rt]*x)%p;//乘以一个数后，加法的lazy标记应该也被乘以这个数 plz[rt]=(plz[rt]*x)%p;//修改乘法lazy return; } pushdown(rt,r-l+1); int m=(l+r)&gt;&gt;1; if(L&lt;=m) mul(L,R,x,lson); if(R&gt;m) mul(L,R,x,rson); pushup(rt); } inline ll query(int L,int R,int l,int r,int rt){ if(L&lt;=l&amp;&amp;R&gt;=r) {return sum[rt];} pushdown(rt,r-l+1);//查询记得将lazy下推 int m=(l+r)&gt;&gt;1; ll ans=0; if(L&lt;=m) ans+=query(L,R,lson); if(R&gt;m) ans+=query(L,R,rson); return ans; } int main() { ios::sync_with_stdio(0);cin.tie(0); cin&gt;&gt;n&gt;&gt;m&gt;&gt;p;Build(1,n,1); for(int i=0;i&lt;m;i++){ int f,a,b,c;cin&gt;&gt;f; if(f==1){ cin&gt;&gt;a&gt;&gt;b&gt;&gt;c;c%=p;mul(a,b,c,1,n,1); } else if(f==2){ cin&gt;&gt;a&gt;&gt;b&gt;&gt;c;c%=p;add(a,b,c,1,n,1); } else{ cin&gt;&gt;a&gt;&gt;b;cout&lt;&lt;query(a,b,1,n,1)%p&lt;&lt;endl; } } } 根号线段树//HDU-4027 根号线段树 题意：两种操作：将某区间每一个数取根号、求出某区间每一个数的和 思路：注意优化，如